sources.scss/main.scss:
@import "reset";
@import "variables";
@import "home";

* {
    box-sizing: border-box;
}

html, body {
    font-size: 18px;
    background: $color1;
    font-family: 'Rasa', sans-serif;
    height: 100%;
}

.menu-icon {
    position: fixed;
    right: 36px;
    top: 36px;
    padding: 20px;
    z-index: 11;

    .fa {
        font-size: 2rem;
        color: $color1;

    }
}

.primary-btn {
    background: $color1;
    -webkit-border-radius: 30px;
    -moz-border-radius: 30px;
    border-radius: 30px;
    text-align: center;
    color: $color2;
    padding: 15px 30px;
    display: inline-block;
    text-transform: uppercase;
    letter-spacing: 3px;
}

.ghost-btn {
    border: 4px solid $color1;
    -webkit-border-radius: 30px;
    -moz-border-radius: 30px;
    border-radius: 30px;
    text-align: center;
    color: $color2;
    padding: 12px 27px;
    display: inline-block;
    text-transform: uppercase;
    letter-spacing: 3px;
    margin-left: 25px;
}

#menu {
    background: $color1;
    position: fixed;
    width: 100%;
    top: 0;
    bottom: 0;
    transform: translate3d(-100%, 0, 0);
    z-index: 10;
    display: flex;
    align-items: center;
    justify-content: center;
    opacity: 1;
    transition: all .5s ease-in-out;

    nav {
        width: 400px;
        text-align: center;

        a {
            font-size: 2.3rem;
            padding: 1rem;
            display: block;
            text-decoration: none;
            color: $color3;

            &:hover {
                color: $color5;
            }
        }
    }
}

.menu-active {

    .menu-icon {
        .fa {
            color: $color3;
        }
    }

    #menu {
        width: 100%;
        top: 0;
        bottom: 0;
        transform: translate3d(0, 0, 0);
    }
}
