.profile {
    a {
        text-decoration: none;
        color: $color2;
    }
}
.primary-btn {
    text-decoration: none;
    background: $color1;
    -webkit-border-radius: 30px;
    -moz-border-radius: 30px;
    border-radius: 30px;
    text-align: center;
    padding: 15px 30px;
    display: inline-block;
    text-transform: uppercase;
    letter-spacing: 3px;
    transition: all .5s ease-in-out;

    &:hover {
        background: $hoverLink;
    }
}

.ghost-btn {
    text-decoration: none;
    border: 4px solid $color1;
    -webkit-border-radius: 30px;
    -moz-border-radius: 30px;
    border-radius: 30px;
    text-align: center;
    color: $color2;
    padding: 12px 27px;
    display: inline-block;
    text-transform: uppercase;
    letter-spacing: 3px;
    margin-left: 25px;
    transition: all .5s ease-in-out;

    &:hover {
        border-color: $hoverLink;
    }
}

#menu {
    background: $color1;
    position: fixed;
    width: 100%;
    top: 0;
    bottom: 0;
    transform: translate3d(-100%, 0, 0);
    z-index: 10;
    display: flex;
    align-items: center;
    justify-content: center;
    opacity: 1;
    transition: all .5s ease-in-out;

    nav {
        width: 400px;
        text-align: center;

        a {
            font-size: 2.3rem;
            padding: 1rem;
            display: block;
            text-decoration: none;
            color: $color3;

            &:hover {
                color: $color5;
            }
        }
    }
}

.menu-active {

    .menu-icon {
        .fa {
            color: black;
        }
    }

    #menu {
        width: 100%;
        text-align: center;
        transform: translate3d(0, 0, 0);
    }
}

.home {
    .dropdown {
        button {
            color: blue;
        }
        align-items: center;
        color: blue
    }

}
